        SHOW_ZOOM_CURSOR: true,
        ENABLE_MARKITUP: true,
        ENABLE_FILE_UPLOAD_PREVIEW: true,
        NOSTALGIA_MODE: false,
        SAVE_DRAFTS: true,
        ENABLE_RANDOM_BUTTON: true,
        SPOILER_ALL_DOWNVOTED: false,
