        $('h2,h3', $panelDiv).css('text-align', 'center');
        $('h2', $panelDiv).css({
            fontWeight: 'bold',
            marginBottom: '0.5em'
        });
        $('h3', $panelDiv).css({
            marginTop: '1em',
            marginBottom: '0.5em'
        });
